import React, { useState, useEffect } from 'react';

const Box = () => {

    const [count, setCount] = useState(0); //setting up the state for the counter

    const handleIncrement = () => setCount(count + 1); //incrementing timer
    const handleDecrement = () => setCount(count - 1); //decrementing timer
    const clearCount = () => setCount(0); //resetting timer

    useEffect(() => {
        document.title=count; //this sets the title of the doc to whatever count is
    }, [count]) //adding count here as a dependence
    //if we don't have a dependency (in array) listed, this will run everytime the component re-renders.
    //if we only want the effect to run under certain circumstances, we enter a dependency. This one, [count], means it only runs when count changes.
    //

    return (
        <>
            <p>{count}</p> {/* {count}  is how we access the 0 from the useState */}
            <button onClick={handleIncrement}>Increment</button>
            <button onClick={handleDecrement}>Decrement</button>
            <button onClick={clearCount}>Reset Timer</button>

        </>
    )
}

export default Box;
